import './App.css';
import {useState, useEffect} from "react";

function App() {

    const [customers, setCustomers] = useState([]);
    const [search, setSearch] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [displaySearh, setDisplaySearch] = useState([]);
    const [searchSubmitted, setSearchSubmitted] = useState(false);
    const [noResultFound, setNoResultFound] = useState(false);
    // const [resultFound, setResultFound] = useState(false);

    const submitSearch = () => {
        setSearchSubmitted(true);
        setSearch('');
        setNoResultFound(true);
    }

    const displayAllCustomers = () => {
        setSearchSubmitted(false);
        setSearchResults([]);
        setNoResultFound(false);
    }
    function getCustomers() {
        fetch('/fetchData')
            .then(response => response.json())
            // .then(data => console.log(data))
            .then(data => {
                console.log(data)
                setCustomers(data)
            })
            .catch(error => console.error(error))
    }

    const handleChange = (event) => {
        setSearch(event.target.value);
        const filterCustomers = customers.filter((item) => {
            if (item.name.toLowerCase() === event.target.value.toLowerCase()){
                // setResultFound(true);
                return item;
            }
        });
        if(filterCustomers.length > 0){
            setSearchResults(filterCustomers[0]);
        } else {
            setSearchResults([]);
            setSearchSubmitted(false);
            setNoResultFound(true);
        }

    }

    useEffect(() => {
        getCustomers();
    }, []);

    useEffect(() => {
        setDisplaySearch(searchResults)
    }, [searchResults])

    return (
    <div className="App">
        <p>Enter Customer Name</p>
        <div>
          <input
            type='text'
            value={search}
            onChange={handleChange}
          />
          <button
            onClick={submitSearch}
          >Submit</button>
            <button
                onClick={displayAllCustomers}
            >Display All Customers
            </button>
        </div>
        <div>
            {searchSubmitted ?
                <p className="customer-title">Searched Customer</p>
                :
                <p className="customer-title">Customers</p>
            }
        </div>
        <div className='result-list'>
            { searchSubmitted && !noResultFound ?
                <ul>
                    <li key={"name"}>Name: {searchResults.name}</li>
                    <li key={"gender"}>Gender: {searchResults.gender}</li>
                    <li key={'age'}>Age: {searchResults.age}</li>
                    <li key={'email'}>Email: {searchResults.email}</li>
                </ul>

                : noResultFound && searchSubmitted ?
                    <p>No Result Found</p> :
                customers.map((item, index) => (
                    <ul>
                        <li key={index + "name"}>Name: {item.name}</li>
                        <li key={index + "gender"}>Gender: {item.gender}</li>
                        <li key={index + 'age'}>Age: {item.age}</li>
                        <li key={index + 'email'}>Email: {item.email}</li>
                    </ul>
                ))
            }
        </div>
    </div>
  );
}

export default App;
